export function drawIcon(ctx: CanvasRenderingContext2D, iconName: string, x: number, y: number, size: number, color: string = '#ffffff') {
  const pathData = iconPaths[iconName];
  if (!pathData) {
    // Fallback to a generic shape if icon not found
    ctx.beginPath();
    ctx.arc(x, y, size / 3, 0, 2 * Math.PI);
    ctx.fillStyle = color;
    ctx.fill();
    return;
  }

  ctx.save();
  
  // Scale and translate to center the icon
  const scale = size / 12; // Icons are designed for 24x24, we use half size for better fit
  ctx.translate(x - size, y - size);
  ctx.scale(scale, scale);
  
  // Create path from SVG data
  const path = new Path2D(pathData);
  
  // Draw the icon
  ctx.fillStyle = color;
  ctx.strokeStyle = color;
  ctx.lineWidth = 0.5;
  ctx.fill(path);
  
  ctx.restore();
}